import React, { useState } from 'react';
import './index.css';
import List from'./keeplist'
const App=()=>{
    const [arr,set]=useState([]);
    const [arr2,set2]=useState({
        title:"",
        des:""
    })
    const [show,set3]=useState(false);
    function Nav(){
        return (<>
            <div className="nav">
            <i class="fa fa-book" aria-hidden="true"></i><span>Asheesh Notes</span>
            </div>
        </>);
    }
    function titlef(event){
        const as=event.target.value;
        set2({
            title:as,
            ...arr2
        });
    }
    function desf(event){
        const as=event.target.value;
        set2({
            ...arr2,
            des:as
        });
    }
    function open(){
        set3(!show);
    }
    function dele(id){

    }

    function Make(){
        return (<>
            <div className="main">
                <div className='main-box'>
                    {!show && <input type='text' placeholder='Title' onClick={open} value={arr2.title}/> }
                    {show && <input type='text' placeholder='Title' onChange={titlef} value={arr2.title}/> }
                    {show && <input type='text' placeholder='Write a note' onChange={desf} value={arr2.des}/>}
                    
                </div>
            </div>
        </>);
    }
    return (<>
        <Nav/>
        <Make/>
        <div className='whole'>
        {arr.map((arrr,index)=>{
            return (<>
                <List key={index}
                des={arrr.des}
                title={arrr.title}
                fun={dele}
                />
            </>);
        })}
        </div>
    </>);
}
export default App;
