import React, { useState } from 'react';
import './index.css';
function App(){
   
    const [num,set]=useState("");
    function Add(){
        
        set(num.id+1);
        return(
        <Box num={num.id} des={num.des}/>
        );
    }
    function text(event){
        const val=event.target.value;      
        set((prev)=>{
            return {id:prev.id,
                des:val
            };
        });
    }
    function Box(props){
        return(
            <>
                <div className='as'>{props.num}. {props.des}</div>
            </>
        );
    }
    return(
        <>
        <div className='cover'>
            <br/>
            <h2>ToDO List</h2>
            <br/>
            <form className='input'><input type="text" placeholder='Add a items' onChange={text}/><input type="submit" className='button' placeholder='Submit'/></form>
            <br/>
            <ol>
            {full.map((old)=>{
                return <li>{old}</li>
            })}
            </ol>
        </div>
        </>
    );
}

export default App;
