import React, { useState } from 'react';
import './index.css';
function App(){
   
    const [num,set]=useState({
        id:0,
        des:""
    });
    function Add(){
        
        set(num.id+1);
        return(
        <Box num={num.id} des={num.des}/>
        );
    }
    function text(event){
        const val=event.target.value;
        set((prev)=>{
            return {id:prev.id,
                des:val
            };
        });
    }

    return(
        <>
        <div className='cover'>
            <br/>
            <h2 className='head'>ToDO List</h2>
            <br/>
            <div className='input'><input type="text" placeholder='Add a items' onChange={text}/><i className="fa fa-plus" onClick={Add}></i></div>
            <Add/>
        </div>
        </>
    );
}
function Box(props){
    return(
        <>
            <div className='as'>{props.num}. {props.des}</div>
        </>
    );
}
export default App;
