import React, { useState } from 'react';
import './index.css';
function App(){
   
    const [num,set]=useState({
        id:0,
        des:""
    });
    function Add(){
        
        set((event)=>{
            return{...num,
            id:1}
        });
        return(
        <Box num={num.id} des={num.des}/>
        );
    }
    function text(event){
        const val=event.target.value;      
        set((prev)=>{
            return {id:prev.id,
                des:val
            };
        });
    }
    function Box(props){
        return(
            <>
                <div className='as'>{props.num}. {props.des}</div>
            </>
        );
    }
    return(
        <>
        <div className='cover'>
            <br/>
            <h2>ToDO List</h2>
            <br/>
            <div className='input'><input type="text" placeholder='Add a items' onChange={text}/><i className="fa fa-plus" onClick={Add}></i></div>
            <br/>
            <Add/>
        </div>
        </>
    );
}

export default App;
